(37953, @GROUP_ID+1, 0, 'How did you--?!  It doesn\'t matter -- I don\'t need a trap to defeat you.', 12, 0, 100, 1, 0, 19605, 0, 'Dark Scout to Player'),
(38029, @GROUP_ID+0, 0, 'Keep them occupied, my brothers!  Allow $n to retrieve the scythe!', 14, 0, 100, 0, 0, 19605, 0, 'Tobias Mistmantle to Player'),
(38029, @GROUP_ID+1, 0, 'Fall back!  Retreat into the woods!', 14, 0, 100, 0, 0, 16419, 0, 'Tobias Mistmantle to Player'),
